ARR = Array.new(5001) { Array.new(5001) }

def commonChild(s1, s2)
    result = nil
    (0..s1.size).each do |n|
        (0..s2.size).each do |m|
            if ARR[n][m]
                next
            end
            if m == 0 or n == 0
                result = 0
            elsif s1[n-1] == s2[m-1]
                result = 1 + ARR[n-1][m-1]
            else
                result = [ARR[n][m-1], ARR[n-1][m]].max
            end
            ARR[n][m] = result
        end
    end
    ARR[s1.size][s2.size]
end

str1 = ''
str2 = ''

puts commonChild(str1, str2)